// SPDX-License-Identifier: MIT
pragma solidity ^0.8.3;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "./lib/LibUtils.sol";
import "./interface/IOracle.sol";

contract NoGas is AccessControl {
    bytes32 public constant OPERATOR_ROLE = keccak256("OPERATOR_ROLE");
    // _feeRate / 10000
    uint256 public _feeRate = 2000;
    address public _refundAddress;
    IOracle public immutable _oracle;

    mapping (address => mapping (bytes32 => bool)) internal txHash;

    event FeeChange(uint256 indexed newFeeRate);
    event RefundAddressChange(address indexed newRefundAddress);
    event AddOperator(address indexed operator);
    event RemoveOperator(address indexed operator);
    event OwnerChanged(address indexed _newOwner);
    event ExchangeInfo(address signer, address token, uint256 tokenAmount, uint256 ethAmount, uint256 transferGasFee);

    /**
     * @notice Throws if the sender is not the owner.
     */
    modifier onlyOwner {
        require(hasRole(DEFAULT_ADMIN_ROLE, msg.sender), "Must be owner");
        _;
    }

    /**
     * @notice Throws if the sender is not the operator.
     */
    modifier onlyOperator {
        require(hasRole(OPERATOR_ROLE, msg.sender), "Must be operator");
        _;
    }

    constructor(address oracle, address refundAddress) {
        _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _oracle = IOracle(oracle);
        _refundAddress = refundAddress;
    }

    // function for owner
    function changeFee(uint256 newFeeRate) external onlyOwner {
        _feeRate = newFeeRate;
        emit FeeChange(newFeeRate);
    }

    function changeRefundAddress(address newRefundAddress) external onlyOwner {
        _refundAddress = newRefundAddress;
        emit RefundAddressChange(newRefundAddress);
    }

    function addOperator(address operator) external onlyOwner {
        _grantRole(OPERATOR_ROLE, operator);
        emit AddOperator(operator);
    }

    function removeOperator(address operator) external onlyOwner {
        _revokeRole(OPERATOR_ROLE, operator);
        emit RemoveOperator(operator);
    }

    function withdraw() external onlyOwner {
        payable(msg.sender).transfer(address(this).balance);
    }

    /**
     * @notice Lets the owner transfer ownership of the contract to a new owner.
     * @param _newOwner The new owner.
     */
    function changeOwner(address _newOwner) external onlyOwner {
        require(_newOwner != address(0), "Address must not be null");
        _grantRole(DEFAULT_ADMIN_ROLE, _newOwner);
        _revokeRole(DEFAULT_ADMIN_ROLE, msg.sender);
        emit OwnerChanged(_newOwner);
    }

    /**
    * @notice Executes a exchange gas transaction.
    * @param token The token for permit and transfer to refund address for gas.
    * @param permit The data for permit.
    * @param signer The signer for param sig.
    * @param sig The signatures.
    * @param exchangeTokenAmount exchange token amount.
    * @param exchangeEthMinLimit exchange eth min limit.
    * @param gasAmount The gas amount off chain.
    */
    function exchangeGas(
        address token,
        bytes memory permit,
        address signer, 
        bytes calldata sig,
        uint256 exchangeTokenAmount,
        uint256 exchangeEthMinLimit,
        uint256 gasAmount
    ) external onlyOperator {
        uint256 startGas = gasleft() + 21000 + msg.data.length * 8;

        bytes32 sigHash = getSignHash(
            token,
            permit,
            signer,
            exchangeTokenAmount,
            exchangeEthMinLimit,
            gasAmount
        );
        require(checkUniqueneHash(signer, sigHash), "Duplicate request");

        verifySignature(sigHash, signer, sig);

        LibUtils.permit(token, permit);

        //calc change amount
        uint256 transferEthAmount = _oracle.outToken(token, exchangeTokenAmount);
        require(transferEthAmount >= exchangeEthMinLimit, "exchange eth less min limit");
        payable(signer).transfer(transferEthAmount);

        uint256 transferGasFee;
//        {
//            //transfer and event
//            uint256 gasConsumed = startGas - gasleft() + 50000;
//            transferGasFee = _oracle.inToken(token, gasConsumed*tx.gasprice);
//            transferGasFee = transferGasFee * (_feeRate + 10000) / 10000;
//            transferGasFee = (transferGasFee < gasAmount) ? transferGasFee : gasAmount;
//        }
        transferGasFee = gasAmount;
        LibUtils.safeTransferFrom(token, signer, _refundAddress, exchangeTokenAmount + transferGasFee);

        emit ExchangeInfo(signer, token, exchangeTokenAmount, 
                 transferEthAmount, transferGasFee);
    }

    function testVerifySignature(
        bytes32 sigHash,
        address signer,
        bytes memory sig
    ) public {
        require(sig.length == 65, "Signature length does not match");
        address recoverSigner = ECDSA.recover(sigHash, sig);
        require((recoverSigner == signer), "Signature verify failed");
    }

    function testGetSignHash(
        address token,
        bytes memory permit,
        address signer,
        uint256 exchangeTokenAmount,
        uint256 exchangeEthMinLimit,
        uint256 gasAmount
    )  public view returns (bytes32) {
        return keccak256(
            abi.encodePacked(
                "\x19Ethereum Signed Message:\n32",
                keccak256(abi.encodePacked(
                    bytes1(0x19),
                    bytes1(0),
                    block.chainid,
                    address(this),
                    token,
                    permit,
                    signer,
                    exchangeTokenAmount,
                    exchangeEthMinLimit,
                    gasAmount))
            ));
    }

    // internal function
    function verifySignature(
        bytes32 sigHash, 
        address signer, 
        bytes memory sig
    ) internal pure {
        require(sig.length == 65, "Signature length does not match");
        address recoverSigner = ECDSA.recover(sigHash, sig);
        require((recoverSigner == signer), "Signature verify failed");
    }

    function getSignHash(
        address token,
        bytes memory permit,
        address signer,
        uint256 exchangeTokenAmount,
        uint256 exchangeEthMinLimit,
        uint256 gasAmount
    )  internal view returns (bytes32) {
        return keccak256(
            abi.encodePacked(
                "\x19Ethereum Signed Message:\n32",
                keccak256(abi.encodePacked(
                    bytes1(0x19),
                    bytes1(0),
                    block.chainid,
                    address(this),
                    token,
                    permit,
                    signer,
                    exchangeTokenAmount,
                    exchangeEthMinLimit,
                    gasAmount))
        ));
    }

    function checkUniqueneHash(address signer, bytes32 hash) internal returns (bool) {
        if (txHash[signer][hash]) {
            return false;
        } else {
            txHash[signer][hash] = true;
            return true;
        }
    }

    // ***************************************************
    receive () external payable {
    }
}

